mapper.update:
UPDATE review_3 SET
rcount= rcount + 1
WHERE no=#{no} AND member_id=#{member_id}

mapper.select:
SELECT rcount
FROM review_3
WHERE no=#{no}

mapper.update:
UPDATE review_3 SET
rcount = rcount - 1
WHERE no=#{no} AND member_id=#{member_id}

mapper.select:
SELECT ROUND(AVG(score),1) 
FROM review_3
WHERE place_no=#{place_no}

SELECT COUNT(*)
FROM review_3
WHERE place_no=#{place_no}

mapper.update:
UPDATE REVIEW_3 SET
content=#{content}
score=#{score} 

WHERE no=#{no}
