mapper.update:
UPDATE review_3 SET
rcount= rcount + 1
WHERE no=#{no} AND member_id=#{member_id}

UPDATE review_3 SET
rcount = rcount - 1

WHERE no=#{no} AND member_id=#{member_id}

mapper.select:
SELECT count(*)
FROM like_check_3
WHERE review_no=#{no} AND member_id=#{member_id}

SELECT ROUND(AVG(score),1) 
FROM review_3
WHERE place_no=#{place_no}

SELECT COUNT(*)
FROM review_3
WHERE place_no=#{place_no}

SELECT place_no,member_id,content,score,(SELECT REP_IMAGE FROM place_3 p WHERE place_no=p.no ) AS rep_image,rcount
   FROM review_3 
   ORDER BY rcount DESC;

mapper.update:
UPDATE REVIEW_3 SET
content=#{content}
score=#{score} 

WHERE no=#{no}
